using System;
using System.Reflection;
using Xamarin.Forms;

namespace Laavor
{
    namespace List
    {
        /// <summary>
        /// Class ListColorControl
        /// </summary>
        public class ListColorControl : Image
        {
            private ImageSource internalSource;

            private ColorUI colorUI;

            /// <summary>
            /// Constructor of ListColorControl
            /// </summary>
            /// <param name="hash"></param>
            /// <param name="color"></param>
            public ListColorControl(string hash, ColorUI color)
            {
                if (hash != "__Laavor*+-")
                {
                    throw new Exception("Key in Invalid in Constructor");
                }

                this.colorUI = color;

                Aspect = Aspect.Fill;
                Margin = new Thickness(2, 0, 2, 0);
                HorizontalOptions = LayoutOptions.FillAndExpand;
                VerticalOptions = LayoutOptions.FillAndExpand;

                byte[] imageBytes = Convert.FromBase64String(GetColor(color));
                var streamImage = new System.IO.MemoryStream(imageBytes);

                this.internalSource = ImageSource.FromStream(() => streamImage);
                Source = internalSource;
            }

            /// <summary>
            /// ChangeColor
            /// </summary>
            /// <param name="hash"></param>
            /// <param name="color"></param>
            public void ChangeColor(string hash, ColorUI color)
            {
                if (hash != "__Laavor*+-")
                {
                    throw new Exception("Key in Invalid in ChangeColor");
                }

                colorUI = color;

                byte[] imageBytes = Convert.FromBase64String(GetColor(colorUI));
                var streamImage = new System.IO.MemoryStream(imageBytes);

                this.internalSource = ImageSource.FromStream(() => streamImage);
                Source = internalSource;
            }

            private string GetFilled(ColorUI color)
            {
                string Black = "";
                string Blue = "";
                string Gray = "";
                string Green = "";
                string Red = "";
                string Yellow = "";
                string Orange = "";
                string Pink = "";
                string White = "";
                string BlueLight = "";
                string YellowLight = "";
                string GreenLight = "";
                string Brown = "";
                string Purple = "";
                string Turquoise = "";
                string PinkLight = "";
                string BlueSky = "";
                string RedLight = "";
                string GrayLight = "";
                string OrangeLight = "";
                string YellowDark = "";
                string GreenDark = "";
                string BlueDark = "";
                string Aqua = "";
                string Tan = "";
                string GreenDarkness = "";
                string BlueViolet = "";

                switch (color)
                {
                    case ColorUI.Blue:
                        return Blue;
                    case ColorUI.Gray:
                        return Gray;
                    case ColorUI.Green:
                        return Green;
                    case ColorUI.Red:
                        return Red;
                    case ColorUI.Yellow:
                        return Yellow;
                    case ColorUI.BlueLight:
                        return BlueLight;
                    case ColorUI.GreenLight:
                        return GreenLight;
                    case ColorUI.YellowLight:
                        return YellowLight;
                    case ColorUI.White:
                        return White;
                    case ColorUI.Pink:
                        return Pink;
                    case ColorUI.Orange:
                        return Orange;
                    case ColorUI.Brown:
                        return Brown;
                    case ColorUI.Purple:
                        return Purple;
                    case ColorUI.Turquoise:
                        return Turquoise;
                    case ColorUI.PinkLight:
                        return PinkLight;
                    case ColorUI.BlueSky:
                        return BlueSky;
                    case ColorUI.Black:
                        return Black;
                    case ColorUI.RedLight:
                        return RedLight;
                    case ColorUI.GrayLight:
                        return GrayLight;
                    case ColorUI.OrangeLight:
                        return OrangeLight;
                    case ColorUI.YellowDark:
                        return YellowDark;
                    case ColorUI.GreenDark:
                        return GreenDark;
                    case ColorUI.BlueDark:
                        return BlueDark;
                    case ColorUI.Aqua:
                        return Aqua;
                    case ColorUI.Tan:
                        return Tan;
                    case ColorUI.GreenDarkness:
                        return GreenDarkness;
                    case ColorUI.BlueViolet:
                        return BlueViolet;
                    default:
                        return BlueSky;
                }

            }

            private string GetColor(ColorUI color)
            {
                MethodInfo methodToInvoke = GetType().GetMethod("GetFilled", BindingFlags.NonPublic | BindingFlags.Instance);

                object[] parametersInvoke = { color };

                var returnStringColor = (string)methodToInvoke.Invoke(this, parametersInvoke);
                return returnStringColor;
            }
        }
    }
}
